import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';
import { connect } from 'react-redux';

const MovieGallery = (props) => {
    switch (props.data.status) {
        case 'START':
            return <h1>LOADING...</h1>;
        case 'FAILED':
            return <h1>FAILED</h1>;
        case 'SUCCESS':
            return (
              <Container>
                <Row>
                  {props.data.data.map((element, idx) => (
                    <Col key={idx} lg="4" md="3" sm="1">
                      <Card>
                        <Card.Img variant="top" src={element.previewURL} />
                        <Card.Body>
                          <Card.Title>{element.user}</Card.Title>
                          <Card.Text>{element.tags}</Card.Text>
                          <Button
                            onClick={() => photoPreview(element.largeImageURL)}
                            variant="primary"
                          >
                            Preview
                          </Button>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                  ))}
                </Row>
              </Container>
            );
        default: return null

    }
}

const mapStateToProps = (state) => {
    return {
        data: state.results
    }
}

export default connect(mapStateToProps)(MovieGallery);
