    switch (props.data.status) {
        case 'START':
            return <h1>LOADING...</h1>;
        case 'FAILED':
            return <h1>FAILED</h1>;
        case 'SUCCESS':
            return (
              <Container>
                <Row>
                </Row>
              </Container>
            );
        default: return null

    }
}

const mapStateToProps = (state) => {
    return {
        data: state.results
    }
}

export default connect(mapStateToProps)(MovieGallery);



