import React from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import { connect } from "react-redux";

const SearchGallery = (props) => {
  switch (props.data.status) {
    case "START":
      return <h1>LOADING...</h1>;
    case "FAILED":
      return <h1>FAILED</h1>;
    case "SUCCESS":
      return (
        <Container>
          <Row>
            {props.data.data.map((elm, idx) => (
              <Col key={idx} className="p-3" lg="3" md="3" sm="1">
                <Card className='m-5' style={{ width: "18rem"}}>
                  <Card.Img className='objectfit' variant="top" src={elm.Poster} height="300" />
                  <Card.Body>
                    <Card.Title
                      style={{ height: "2rem" }}
                      className="text-center"
                    >
                      <h6>{elm.Title}</h6>
                    </Card.Title>
                    {/* <Card.Text className="text-center">{elm.Type}</Card.Text> */}
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      );
    default:
      return null;
  }
};

const mapStateToProps = (state) => {
  return {
    data: state.results,
  };
};

export default connect(mapStateToProps)(SearchGallery);
