import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';
import { connect } from 'react-redux';

const MovieGallery = (props) => {
    switch (props.data.status) {
        case 'START':
            return <h1>LOADING...</h1>;
        case 'FAILED':
            return <h1>FAILED</h1>;
        case 'SUCCESS':
            return (
              <Container>
                <Row>
                  {props.data.data.map((elm, idx) => (
                    <Col key={idx}  lg="4" md="3" sm="1">
                      <Card style={{ width: "20rem" ,height:'25rem'}}>
                        <Card.Img variant="top" src={elm.Poster} height="200" />
                        <Card.Body>
                          <Card.Title>{elm.Title}</Card.Title>
                          <Card.Text style={{ minHeight: "70px" }}>
                            {elm.Type}
                          </Card.Text>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </Container>
            );
        default: return null

    }
}

const mapStateToProps = (state) => {
    return {
        data: state.results
    }
}

export default connect(mapStateToProps)(MovieGallery);
