import { combineReducers } from "redux";

let searchResult = {
  data: [],
  status: "",
  selectedIndex: -1

};

const searchReducer = (results = searchResult, action) => {
  console.log(action);

  switch (action.type) {
    case "START":
      searchResult = {
        data: [], status: "START", selectedIndex: -1
 };
      return { ...searchResult };
    case "SUCCESS":
      searchResult = { data: action.payload.Search, status: "SUCCESS" };
      return { ...searchResult };
    case "FAILED":
      searchResult = { data: [], status: "FAILED" };
      return { ...searchResult };
    case 'SELECT_INDEX':
      searchResult.selectedIndex = action.payload;
      return { ...searchResult }
    default:
      return results;
  }
};

let movieSearchResult = {
  data: [],
  status: "",
};

const movieSearchReducer = (results = movieSearchResult, action) => {
  console.log(action);

  switch (action.type) {
    case "MOVIE_START":
      searchResult = { data: [], status: "START" };
      return { ...searchResult };
    case "MOVIE_SUCCESS":
      searchResult = { data: action.payload.Search, status: "SUCCESS" };
      return { ...searchResult };
    case "MOVIE_FAILED":
      searchResult = { data: [], status: "FAILED" };
      return { ...searchResult };
    default:
      return results;
  }
};

let seriesSearchResult = {
  data: [],
  status: "",
};

const seriesSearchReducer = (results = seriesSearchResult, action) => {
  console.log(action);

  switch (action.type) {
    case "SERIES_START":
      searchResult = { data: [], status: "START" };
      return { ...searchResult };
    case "SERIES_SUCCESS":
      searchResult = { data: action.payload.Search, status: "SUCCESS" };
      return { ...searchResult };
    case "SERIES_FAILED":
      searchResult = { data: [], status: "FAILED" };
      return { ...searchResult };
    default:
      return results;
  }
};

let favList = {
  data:[]
};

const favReducer = (results=favList,action) =>{
  switch(action.type){
    case 'ADD_TO_FAV':
      favList={data:action.payload.elm};
      return {...favList};
    default:
      return results
  }
}

export default combineReducers({
  results: searchReducer,
  movieResults: movieSearchReducer,
  seriesResults: seriesSearchReducer,
  favResults: favReducer
});
